$(document).ready(function(){$('.formula-link').on('click',function(e){e.preventDefault();var formulaId=$(this).data('id');$.get('api/'+formulaId,function(data){$('#main-content').data('formulaData',data);$('#main-content').html(data);});});});$('#create-button').on('click',function(){var formData={name:'',description:'',ingredients:[]};$.ajax({url:'/api/new/',type:'POST',data:JSON.stringify(formData),contentType:'application/json; charset=utf-8',dataType:'json',success:function(data){window.location.href='/edit-formula/'+data.id;}});});;